INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Comanche 4', 3, 2001, 'Simulador', 'PC', 'https://raw.githubusercontent.com/fabiuniz/dslist-rest/main/images/11.png', 'Um simulador de helicóptero de combate realista.', 'Comanche 4 é um jogo de simulação de helicóptero que coloca o jogador no controle de um helicóptero de ataque Comanche RAH-66, participando de missões de combate em vários cenários.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Days Gone', 5, 2019, 'Ação-aventura', 'PS4, PC', 'https://raw.githubusercontent.com/fabiuniz/dslist-rest/main/images/12.png', 'Sobreviva em um mundo pós-apocalíptico infestado por Freakers.', 'Days Gone é um jogo de ação-aventura e sobrevivência ambientado em um mundo aberto pós-apocalíptico, onde o jogador assume o papel de Deacon St. John, um motociclista e ex-membro de uma gangue, lutando para sobreviver contra hordas de criaturas hostis.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('DiRT', 2, 2007, 'Corrida', 'PC, Xbox 360, PS3', 'https://raw.githubusercontent.com/fabiuniz/dslist-rest/main/images/13.png', 'Um jogo de corrida off-road com gráficos impressionantes.', 'DiRT é um jogo de corrida off-road que oferece uma experiência emocionante com diversos veículos e pistas, incluindo rali, rallycross e eventos de buggy.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Evolution GT', 2, 2006, 'Corrida', 'PC, PS2', 'https://raw.githubusercontent.com/fabiuniz/dslist-rest/main/images/14.png', 'Um jogo de corrida focado na evolução do piloto e seus carros.', 'Evolution GT é um jogo de corrida que combina simulação e elementos de RPG, onde o jogador desenvolve suas habilidades e customiza seus carros para se tornar o melhor piloto.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('FlatOut Ultimate Carnage', 3, 2007, 'Corrida', 'PC, Xbox 360', 'https://raw.githubusercontent.com/fabiuniz/dslist-rest/main/images/15.png', 'Corrida arcade com destruição intensa e bonecos de crash-test voadores.', 'FlatOut Ultimate Carnage é a versão definitiva de FlatOut 2, trazendo corridas de demolição com física de danos aprimorada e modos de jogo caóticos.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Flatout Utimate', 4, 2007, 'Corrida', 'PC, Xbox 360', 'https://raw.githubusercontent.com/fabiuniz/dslist-rest/main/images/16.png', 'Corrida de demolição com muita ação e destruição.', 'Flatout Ultimate (provavelmente FlatOut Ultimate Carnage) oferece corridas intensas com foco na destruição de veículos e ambientes interativos.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('FlatOut', 4, 2004, 'Corrida', 'PC, PS2, Xbox', 'https://raw.githubusercontent.com/fabiuniz/dslist-rest/main/images/17.png', 'O jogo original de corrida e demolição com física realista.', 'FlatOut é um jogo de corrida que se destaca pela sua física de destruição detalhada, onde o objetivo é não apenas vencer, mas também causar o máximo de estrago.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Forza Horizon 5', 5, 2021, 'Corrida', 'Xbox Series X/S, Xbox One, PC', 'https://raw.githubusercontent.com/fabiuniz/dslist-rest/main/images/18.png', 'Aventura de corrida em mundo aberto no México vibrante.', 'Forza Horizon 5 leva os jogadores a um mundo aberto deslumbrante ambientado no México, com centenas de carros, eventos e paisagens para explorar.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('God of War', 4, 2018, 'Ação-aventura', 'PS4, PC', 'https://raw.githubusercontent.com/fabiuniz/dslist-rest/main/images/19.png', 'Kratos e Atreus em uma jornada épica pela mitologia nórdica.', 'God of War reinventa a série com uma nova perspectiva e foco na relação entre Kratos e seu filho Atreus, enquanto eles enfrentam deuses e monstros da mitologia nórdica.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Grand Prix 4', 2, 2002, 'Corrida', 'PC', 'https://raw.githubusercontent.com/fabiuniz/dslist-rest/main/images/20.png', 'Simulador de Fórmula 1 altamente aclamado.', 'Grand Prix 4 é um dos simuladores de Fórmula 1 mais realistas e detalhados já feitos, oferecendo uma experiência autêntica das corridas da F1.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('GTA5', 3, 2013, 'Ação-aventura', 'PC, PS3, PS4, Xbox 360, Xbox One, PS5, Xbox Series X/S', 'https://raw.githubusercontent.com/fabiuniz/dslist-rest/main/images/21.png', 'Três criminosos em um mundo aberto gigante de Los Santos.', 'Grand Theft Auto V é um jogo de ação-aventura em mundo aberto onde os jogadores controlam três protagonistas interligados em uma história complexa de crime em Los Santos e Blaine County.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Horizon Zero Dawn Complete Edition', 0, 2017, 'RPG de Ação', 'PS4, PC', 'https://raw.githubusercontent.com/fabiuniz/dslist-rest/main/images/22.png', 'Aloy em um mundo pós-apocalíptico dominado por máquinas.', 'Horizon Zero Dawn Complete Edition inclui o jogo base e a expansão Frozen Wilds, onde a caçadora Aloy explora um mundo exuberante e misterioso habitado por máquinas robóticas.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Matrix', 3, 1999, 'Filme/Ação-aventura', 'PC, PS2, Xbox (como The Matrix: Path of Neo)', 'https://raw.githubusercontent.com/fabiuniz/dslist-rest/main/images/23.png', 'Inspirado no universo de ficção científica de Matrix.', 'Embora "Matrix" seja primariamente um filme, jogos como "Enter the Matrix" e "The Matrix: Path of Neo" exploram o universo cinematográfico, com combate baseado nas habilidades de Neo.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Need For Speed Carbon', 3, 2006, 'Corrida', 'PC, PS2, Xbox 360, PS3, Wii', 'https://raw.githubusercontent.com/fabiuniz/dslist-rest/main/images/24.png', 'Domine as ruas e territórios em corridas de canyon.', 'Need For Speed Carbon foca em corridas de rua ilegais e domínio de territórios, introduzindo corridas de canyon onde os jogadores precisam superar rivais em descidas perigosas.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Need For Speed Hot Pursuit 2', 3, 2002, 'Corrida', 'PC, PS2, Xbox, GameCube', 'https://raw.githubusercontent.com/fabiuniz/dslist-rest/main/images/25.png', 'Corridas de alta velocidade e perseguições policiais emocionantes.', 'Need For Speed Hot Pursuit 2 traz de volta as perseguições policiais clássicas e corridas exóticas em cenários diversos, com um forte foco em velocidade e fugas.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Need For Speed Most Wanted', 4, 2005, 'Corrida', 'PC, PS2, Xbox, Xbox 360, GameCube', 'https://raw.githubusercontent.com/fabiuniz/dslist-rest/main/images/26.png', 'Fuja da polícia e suba na Blacklist para se tornar o mais procurado.', 'Need For Speed Most Wanted combina corridas de rua em mundo aberto com perseguições policiais intensas, onde o objetivo é subir na Blacklist e se tornar o piloto mais notório.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Need for Speed Undercover', 4, 2008, 'Corrida', 'PC, PS2, PS3, Xbox 360, Wii', 'https://raw.githubusercontent.com/fabiuniz/dslist-rest/main/images/27.png', 'Pilote infiltrado para derrubar uma sindicato do crime internacional.', 'Need for Speed Undercover coloca o jogador como um agente secreto infiltrado em uma organização criminosa, participando de corridas de rua para desvendar uma conspiração.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Need For Speed Underground 2', 3, 2004, 'Corrida', 'PC, PS2, Xbox, GameCube', 'https://raw.githubusercontent.com/fabiuniz/dslist-rest/main/images/28.png', 'Personalização profunda e exploração em mundo aberto.', 'Need For Speed Underground 2 expande o conceito do seu antecessor com um mundo aberto para explorar, permitindo customização extensa de carros e diversos modos de corrida.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Need For Speed Underground', 3, 2003, 'Corrida', 'PC, PS2, Xbox, GameCube', 'https://raw.githubusercontent.com/fabiuniz/dslist-rest/main/images/29.png', 'O renascimento das corridas de rua e tunagem de carros.', 'Need For Speed Underground revolucionou a série com um foco em corridas de rua noturnas, personalização detalhada de carros e uma trilha sonora marcante.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Resident Evil Village', 4, 2021, 'Survival Horror', 'PC, PS4, PS5, Xbox One, Xbox Series X/S', 'https://raw.githubusercontent.com/fabiuniz/dslist-rest/main/images/30.png', 'Ethan Winters enfrenta horrores em uma vila sombria e misteriosa.', 'Resident Evil Village continua a história de Ethan Winters, que busca sua filha sequestrada em uma vila remota e aterrorizante, enfrentando criaturas góticas e monstros em primeira pessoa.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Senhor dos Aneis', 3, 2002, 'RPG/Ação-aventura', 'PC, PS2, Xbox, GameCube (jogos variados)', 'https://raw.githubusercontent.com/fabiuniz/dslist-rest/main/images/31.png', 'Baseado na épica saga de fantasia de J.R.R. Tolkien.', 'Diversos jogos foram lançados baseados em "O Senhor dos Anéis", abrangendo gêneros de RPG de ação, estratégia e aventura, permitindo aos jogadores vivenciar a Terra Média.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('The Last of Us - Part I', 5, 2013, 'Ação-aventura', 'PS3, PS4, PS5, PC', 'https://raw.githubusercontent.com/fabiuniz/dslist-rest/main/images/32.png', 'Joel e Ellie em uma jornada brutal por um EUA pós-apocalíptico.', 'The Last of Us - Part I é uma recriação do aclamado jogo original, que segue Joel e Ellie em sua emocionante e perigosa jornada através de um mundo infestado por infectados e sobreviventes hostis.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('The Matrix - Path of Neo', 2, 2005, 'Ação-aventura', 'PC, PS2, Xbox', 'https://raw.githubusercontent.com/fabiuniz/dslist-rest/main/images/33.png', 'Reviva os momentos icônicos da trilogia Matrix como Neo.', 'The Matrix - Path of Neo permite aos jogadores controlar Neo e reviver os eventos mais importantes dos três filmes da trilogia Matrix, com combate aprimorado e habilidades de Neo.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Príncipe da Pérsia', 0, 1989, 'Plataforma', 'PC, Amiga, NES, SNES, Master System, Mega Drive, Mobile', 'https://raw.githubusercontent.com/fabiuniz/dslist-rest/main/images/principedapersia.png', 'Um clássico jogo de plataforma que combina quebra-cabeças e combate.', 'Príncipe da Pérsia é um marco nos jogos de plataforma, conhecido por suas animações realistas, design de níveis desafiador e uma mistura única de exploração, quebra-cabeças e combate com espadas. O jogador deve correr contra o tempo para resgatar a princesa do malvado Vizir Jaffar.');
